    private void txtEurosFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_txtEurosFocusLost
        if (!"".equals(txtEuros.getText())) {
            btnConvertir.setActionCommand("cambiaAPesetas");
        }
    }//GEN-LAST:event_txtEurosFocusLost

    private void txtPesetasFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_txtPesetasFocusLost
        if (!"".equals(txtPesetas.getText())) {
            btnConvertir.setActionCommand("cambiaAEuros");
        }
    }//GEN-LAST:event_txtPesetasFocusLost

