    public void setOyente(PrincipalLOG oyente) {
        btnConvertir.addActionListener(oyente);
        btnLimpiar.addActionListener(oyente);
    }

    public String getEuros() {
        return txtEuros.getText();
    }

    public String getPesetas() {
        return txtPesetas.getText();
    }

    public void setEuros(String euros) {
        txtEuros.setText(euros);
    }

    public void setPesetas(String pesetas) {
        txtPesetas.setText(pesetas);
    }

    public void limpiaPesetas() {
        txtPesetas.setText("");
        txtPesetas.grabFocus();
    }
    
    public void limpiaEuros() {
        txtEuros.setText("");
        txtEuros.grabFocus();
    }

